		delete from trn_delivery_order_head where no_delivery_order = var_no_delivery_order;
		
		insert into trn_delivery_order_head(
			 no_delivery_order,
			 no_sales_order,
			 id_customer,
			 date_trn,
			 notes,
			 modified_user,
			 created_date,
