			)
		values(var_no_journal,
					 var_date_trn,
					 var_comment,
				   var_created_user,
					 var_created_date,
					 var_modified_user,
					 var_modified_date,
					 'General Journal',
						var_total);
	elseif var_detail = 1 THEN
		if varloop = 0 then
			delete from trn_journal_det where journal_no = var_no_journal;			
