	select id_item,case when sum(qty) is null then 0 else sum(qty) end as TotalJual 
	from i_stock_out GROUP BY id_item order by id_item) d 
on d.id_item = a.id_item
where a.id_warehouse = var_gudang;


COMMIT;
END */;;
